import React, { useState } from 'react';
import "./formStyle.css"
import { Link,useNavigate } from "react-router-dom";
//https://localhost:7079/api/User/login?username=${formData.username}&password=${formData.password} <------------login request


const LoginForm = () => {
  const  navigate=useNavigate();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const handleInputChange = (event) => {
    setFormData({
      ...formData,  
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!formData.username || !formData.password) {
    alert("Please enter both username and password");
    return;
    }
    try {
    const response = await fetch(`https://localhost:7079/api/User/login?username=$username&password=$password`, {
    method: "POST",
    headers: {
    "Content-Type": "application/json",
    },
    });
    if (!response.ok) {
    throw new Error(`Failed to log in, status code: ${response.status}`);
    }
    const result = await response.json();
    console.log(result);
    if (result.status === 'success') {
    if(formData.username === 'admin') {
    navigate("BackOffice");
    } else {
    navigate("MainPage");
    }
    } else {
    throw new Error("Incorrect username or password");
    }
    } catch (error) {
    console.error(error);
    alert(error.message);
    }
    };
  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="username"
        placeholder="Username"
        value={formData.username}
        onChange={handleInputChange}
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={formData.password}
        onChange={handleInputChange}
      />
      <button type="submit">Submit</button>
      <Link to='/RegisterForm'>Don't have an account?</Link>
    </form>
  );
};

export default LoginForm;
