import React, { useState } from 'react';
import "./formStyle.css"
import { useNavigate } from "react-router-dom";
//https://localhost:7079/api/User/login?username=${formData.username}&password=${formData.password} <------------login request


const LoginForm = () => {
  const  navigate=useNavigate();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const handleInputChange = (event) => {
    setFormData({
      ...formData,  
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`https://localhost:7079/api/User/login?username=${formData.username}&password=${formData.password}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      if (!response.ok) {
        throw new Error(`Failed to log in, status code: ${response.status}`);
      }
      const result = await response.json();
      if (result.status === 'success') {
        if(formData.username === 'admin') {
          navigate("BackOffice");
        } else {
          navigate("MainPage");
        }
      } else {
        throw new Error(`Failed to log in, status: ${result.status}`);
      }
    } catch (error) {
      console.error(error);
      alert(error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="username"
        placeholder="Username"
        value={formData.username}
        onChange={handleInputChange}
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={formData.password}
        onChange={handleInputChange}
      />
      <button type="submit">Submit</button>
    </form>
      //<Link to=('RegisterForm')>Don't have an account?</Link>
  );
};

export default LoginForm;
